import React, { useState, useEffect, useMemo } from 'react';
import DashboardLayout from '../components/Layout/DashboardLayout';
import { useAuth } from '../context/AuthContext';
import {
    MessageSquare,
    Rocket,
    Swords,
    Trophy,
    BrainCircuit,
    CalendarClock,
    Flame,
    Sparkles
} from 'lucide-react';
import { Link } from 'react-router-dom';
import { progressAPI, revisionsAPI, gamificationAPI, coursesAPI } from '../services/api';

const BADGE_ICON_MAP = {
    'ðŸ”¥': Flame,
    'âš”ï¸': Swords,
    'ðŸš€': Rocket,
    'ðŸ†': Trophy,
    'ðŸ§ ': BrainCircuit,
    'âœ¨': Sparkles
};
const BADGE_ACCENT_MAP = {
    streak: 'bg-[#fde68a] text-[#92400e]',
    mastery: 'bg-[#c7d2fe] text-[#3730a3]',
    revision: 'bg-[#bfdbfe] text-[#1d4ed8]',
    special: 'bg-[#dcfce7] text-[#166534]'
};

const StudentDashboard = () => {
    const { user } = useAuth();
    const [knowledgeNodes, setKnowledgeNodes] = useState([]);
    const [revisionPlan, setRevisionPlan] = useState([]);
    const [gamification, setGamification] = useState(null);
    const [progressStats, setProgressStats] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchAll = async () => {
            try {
                const [coursesRes, revsRes, gamRes, statsRes] = await Promise.all([
                    coursesAPI.getMyCourses(),
                    revisionsAPI.getMyRevisions({ status: 'pending,overdue', limit: 5 }),
                    gamificationAPI.getMyProfile(),
                    progressAPI.getStats()
                ]);

                // Knowledge map: fetch first enrolled course's knowledge map
                const enrolled = coursesRes.data?.data || [];
                if (enrolled.length > 0) {
                    try {
                        const mapRes = await progressAPI.getKnowledgeMap(enrolled[0]._id);
                        const nodes = mapRes.data?.data?.nodes || [];
                        setKnowledgeNodes(
                            nodes.slice(0, 6).map((n) => ({
                                id: n.topicId,
                                label: n.title,
                                mastery: n.mastery ?? 0,
                                prerequisites: n.prerequisites?.length ?? 0,
                                status: n.mastery >= 0.8 ? 'strong' : n.mastery >= 0.5 ? 'medium' : 'weak'
                            }))
                        );
                    } catch { /* no map data yet */ }
                }

                // Revisions
                const revisions = revsRes.data?.data || [];
                setRevisionPlan(
                    revisions.map((r) => {
                        const risk = r.priority === 'critical' || r.priority === 'high' ? 'Critical' : r.priority === 'medium' ? 'Moderate' : 'Low';
                        const scheduledDate = new Date(r.scheduledFor);
                        const today = new Date();
                        const diffDays = Math.ceil((scheduledDate - today) / (1000 * 60 * 60 * 24));
                        const next = diffDays <= 0 ? 'Today' : diffDays === 1 ? 'Tomorrow' : scheduledDate.toLocaleDateString('en-US', { weekday: 'short' });
                        const tag = diffDays <= 0 ? 'Urgent' : diffDays <= 2 ? 'Upcoming' : 'Stable';
                        return {
                            concept: r.topic?.title || 'Topic',
                            mastery: `${Math.round((r.topic?.mastery ?? 0.5) * 100)}%`,
                            risk,
                            next,
                            tag
                        };
                    })
                );

                setGamification(gamRes.data?.data || null);
                setProgressStats(statsRes.data?.data || null);
            } catch {
                // gracefully degrade with empty data
            } finally {
                setLoading(false);
            }
        };
        fetchAll();
    }, []);

    const xp = gamification?.totalPoints ?? 0;
    const level = gamification?.level ?? 1;
    const streakDays = gamification?.currentStreak ?? 0;
    const syllabusCompletion = progressStats
        ? (progressStats.totalTopics > 0 ? (progressStats.completed + progressStats.mastered) / progressStats.totalTopics : 0)
        : 0;

    const progressMomentum = [
        { label: 'Concept Mastery', value: Math.round((progressStats?.averageMastery ?? 0) * 100) },
        { label: 'Topics Completed', value: progressStats?.totalTopics > 0 ? Math.round(((progressStats?.completed ?? 0) / progressStats.totalTopics) * 100) : 0 },
        { label: 'Active Study Rate', value: progressStats?.totalTopics > 0 ? Math.round(((progressStats?.inProgress ?? 0 + (progressStats?.completed ?? 0) + (progressStats?.mastered ?? 0)) / progressStats.totalTopics) * 100) : 0 }
    ];

    const badges = useMemo(() => {
        if (!gamification?.badges?.length) return [
            { title: 'Streak Master', description: `${streakDays} day revision streak`, icon: Flame, accent: 'bg-[#fde68a] text-[#92400e]' },
            { title: 'Keep Learning', description: 'Complete topics to earn badges', icon: Trophy, accent: 'bg-[#c7d2fe] text-[#3730a3]' }
        ];
        return gamification.badges.map((b) => ({
            title: b.name,
            description: b.description,
            icon: BADGE_ICON_MAP[b.icon] || Trophy,
            accent: BADGE_ACCENT_MAP[b.category] || 'bg-[#c7d2fe] text-[#3730a3]'
        }));
    }, [gamification, streakDays]);

    const emptyStates = {
        knowledgeMap: 'No concepts loaded yet. Enroll in a course to see your knowledge map.',
        revisions: 'No pending revisions. New recommendations appear as you study.'
    };

    if (loading) {
        return (
            <DashboardLayout>
                <div className="flex items-center justify-center py-24">
                    <div className="h-10 w-10 animate-spin rounded-full border-4 border-[#e2e8f0] border-t-[#4338ca]" />
                </div>
            </DashboardLayout>
        );
    }

    return (
        <DashboardLayout>
            <div className="space-y-6">
                <section className="rounded-[28px] bg-gradient-to-br from-[#ede9fe] via-[#f0f9ff] to-[#fef9c3] p-6 shadow-xl">
                    <div className="flex flex-col gap-6 lg:flex-row lg:items-center lg:justify-between">
                        <div>
                            <p className="text-xs font-semibold uppercase tracking-[0.32em] text-[#4338ca]">
                                Personalized learning overview
                            </p>
                            <h1 className="mt-3 text-3xl font-semibold text-[#0f172a]">
                                Welcome back, {user?.name ?? 'Learner'}
                            </h1>
                            <p className="mt-2 text-sm text-[#475569]">
                                Your knowledge graph has evolved overnight. Review the adaptive revision plan and keep the streak alive.
                            </p>
                        </div>
                        <div className="grid gap-3 rounded-3xl bg-white/80 p-5 shadow-lg">
                            <div className="flex items-center justify-between">
                                <span className="text-xs uppercase tracking-[0.28em] text-[#64748b]">XP</span>
                                <span className="text-lg font-semibold text-[#111827]">{xp.toLocaleString()}</span>
                            </div>
                            <div className="flex items-center justify-between">
                                <span className="text-xs uppercase tracking-[0.28em] text-[#64748b]">Level</span>
                                <span className="text-lg font-semibold text-[#4338ca]">{level}</span>
                            </div>
                            <div>
                                <div className="flex items-center justify-between text-xs text-[#475569]">
                                    <span>Syllabus completion</span>
                                    <span>{Math.round(syllabusCompletion * 100)}%</span>
                                </div>
                                <div className="mt-2 h-2 rounded-full bg-[#e2e8f0]">
                                    <div
                                        className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] via-[#2563eb] to-[#0ea5e9] transition-all"
                                        style={{ width: `${Math.round(syllabusCompletion * 100)}%` }}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

                <section className="space-y-6">
                    <div className="grid gap-6">
                        <section className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                            <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
                                <div>
                                    <h2 className="text-lg font-semibold text-[#0f172a]">Live knowledge map</h2>
                                    <p className="text-sm text-[#475569]">
                                        Nodes glow by mastery. Hover for relationships and recommended actions.
                                    </p>
                                </div>
                                <button className="rounded-full bg-[#4338ca] px-5 py-2 text-sm font-semibold text-white shadow transition hover:-translate-y-0.5 hover:bg-[#312e81]">
                                    View full map
                                </button>
                            </div>
                            <div className="mt-6 grid gap-4 md:grid-cols-3">
                                {knowledgeNodes.map((node) => (
                                    <div
                                        key={node.id}
                                        className="relative overflow-hidden rounded-3xl border border-[#e2e8f0] bg-[#f8fafc] p-5 shadow transition hover:-translate-y-1 hover:shadow-2xl"
                                    >
                                        <span className="text-xs uppercase tracking-[0.28em] text-[#94a3b8]">{node.label}</span>
                                        <p className="mt-3 text-2xl font-semibold text-[#111827]">
                                            {Math.round(node.mastery * 100)}%
                                        </p>
                                        <p className="text-xs text-[#475569]">Prerequisites: {node.prerequisites || 'None'}</p>
                                        <span
                                            className={`absolute right-4 top-4 rounded-full px-3 py-1 text-xs font-semibold ${node.status === 'weak'
                                                ? 'bg-[#fee2e2] text-[#b91c1c]'
                                                : node.status === 'medium'
                                                    ? 'bg-[#fef9c3] text-[#92400e]'
                                                    : 'bg-[#dcfce7] text-[#166534]'
                                                }`}
                                        >
                                            {node.status}
                                        </span>
                                    </div>
                                ))}
                                {!knowledgeNodes.length && (
                                    <div className="rounded-3xl bg-[#f1f5f9] p-6 text-sm text-[#64748b]">
                                        {emptyStates.knowledgeMap}
                                    </div>
                                )}
                            </div>
                        </section>

                        <section className="grid gap-6 lg:grid-cols-[1.25fr,0.75fr]">
                            <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                                <div className="flex items-center justify-between">
                                    <h2 className="text-lg font-semibold text-[#0f172a]">Adaptive revision table</h2>
                                    <span className="rounded-full bg-[#fee2e2] px-4 py-1 text-xs font-semibold uppercase tracking-widest text-[#b91c1c]">
                                        AI Generated
                                    </span>
                                </div>
                                <div className="mt-6 overflow-hidden rounded-2xl border border-[#e2e8f0]">
                                    <table className="w-full text-sm text-[#334155]">
                                        <thead className="bg-[#f8fafc] text-xs uppercase tracking-[0.18em] text-[#64748b]">
                                            <tr>
                                                <th className="px-4 py-3 text-left">Concept</th>
                                                <th className="px-4 py-3 text-left">Mastery</th>
                                                <th className="px-4 py-3 text-left">Forget risk</th>
                                                <th className="px-4 py-3 text-left">Next revision</th>
                                                <th className="px-4 py-3 text-right">Tag</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {revisionPlan.map((row) => (
                                                <tr key={row.concept} className="border-t border-[#e2e8f0] bg-white transition hover:bg-[#f8fafc]">
                                                    <td className="px-4 py-3 font-semibold text-[#111827]">{row.concept}</td>
                                                    <td className="px-4 py-3">{row.mastery}</td>
                                                    <td className="px-4 py-3">{row.risk}</td>
                                                    <td className="px-4 py-3">{row.next}</td>
                                                    <td className="px-4 py-3 text-right">
                                                        <span
                                                            className={`rounded-full px-3 py-1 text-xs font-semibold ${row.tag === 'Urgent'
                                                                ? 'bg-[#fee2e2] text-[#b91c1c]'
                                                                : row.tag === 'Stable'
                                                                    ? 'bg-[#dcfce7] text-[#166534]'
                                                                    : 'bg-[#fef9c3] text-[#92400e]'
                                                                }`}
                                                        >
                                                            {row.tag}
                                                        </span>
                                                    </td>
                                                </tr>
                                            ))}
                                            {!revisionPlan.length && (
                                                <tr>
                                                    <td colSpan={5} className="px-4 py-6 text-center text-sm text-[#94a3b8]">
                                                        {emptyStates.revisions}
                                                    </td>
                                                </tr>
                                            )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>

                            <div className="grid gap-6">
                                <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                                    <div className="flex items-center justify-between">
                                        <h2 className="text-lg font-semibold text-[#0f172a]">Weakness heatmap</h2>
                                        <Sparkles className="h-5 w-5 text-[#4338ca]" />
                                    </div>
                                    <div className="mt-4 grid grid-cols-5 gap-2">
                                        {[33, 41, 56, 68, 82, 45, 27, 74, 59, 91].map((score, index) => (
                                            <div
                                                key={index}
                                                className="flex h-16 items-center justify-center rounded-2xl text-xs font-semibold text-white transition hover:-translate-y-0.5"
                                                style={{ background: `rgba(67, 56, 202, ${Math.max(0.35, score / 100)})` }}
                                            >
                                                {score}%
                                            </div>
                                        ))}
                                    </div>
                                    <div className="mt-4 flex items-center justify-between text-xs text-[#64748b]">
                                        <span>Low mastery</span>
                                        <span>High mastery</span>
                                    </div>
                                </div>

                                <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                                    <h2 className="text-lg font-semibold text-[#0f172a]">Gamified progression</h2>
                                    <div className="mt-4 grid gap-3">
                                        <div className="flex items-center justify-between rounded-2xl bg-[#f8fafc] p-4">
                                            <div>
                                                <p className="text-xs uppercase tracking-widest text-[#475569]">XP Counter</p>
                                                <p className="text-lg font-semibold text-[#111827]">{xp.toLocaleString()}</p>
                                            </div>
                                            <Trophy className="h-6 w-6 text-[#f59e0b]" />
                                        </div>
                                        <div className="flex items-center justify-between rounded-2xl bg-[#f8fafc] p-4">
                                            <div>
                                                <p className="text-xs uppercase tracking-widest text-[#475569]">Level</p>
                                                <p className="text-lg font-semibold text-[#111827]">{level}</p>
                                            </div>
                                            <BrainCircuit className="h-6 w-6 text-[#4338ca]" />
                                        </div>
                                        <div className="flex items-center justify-between rounded-2xl bg-[#f8fafc] p-4">
                                            <div>
                                                <p className="text-xs uppercase tracking-widest text-[#475569]">Mastery streak</p>
                                                <p className="text-lg font-semibold text-[#111827]">{streakDays} days</p>
                                            </div>
                                            <Flame className="h-6 w-6 text-[#ef4444]" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </section>

                        <section className="grid gap-6 lg:grid-cols-[1.1fr,0.9fr]">
                            <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                                <h2 className="text-lg font-semibold text-[#0f172a]">Progress momentum</h2>
                                <div className="mt-5 space-y-4">
                                    {progressMomentum.map((item) => (
                                        <div key={item.label} className="rounded-2xl bg-[#f8fafc] p-4">
                                            <div className="flex items-center justify-between">
                                                <span className="text-sm font-semibold text-[#111827]">{item.label}</span>
                                                <span className="text-sm font-semibold text-[#4338ca]">{item.value}%</span>
                                            </div>
                                            <div className="mt-3 h-2 rounded-full bg-[#e2e8f0]">
                                                <div
                                                    className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] to-[#2563eb]"
                                                    style={{ width: `${item.value}%` }}
                                                />
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                                <div className="flex items-center justify-between">
                                    <h2 className="text-lg font-semibold text-[#0f172a]">Badge cabinet</h2>
                                    <Sparkles className="h-5 w-5 text-[#f59e0b]" />
                                </div>
                                <div className="mt-5 grid gap-4">
                                    {badges.map((badge) => (
                                        <div
                                            key={badge.title}
                                            className={`flex items-center gap-4 rounded-3xl ${badge.accent} p-4 shadow transition hover:-translate-y-1`}
                                        >
                                            <badge.icon className="h-6 w-6" />
                                            <div>
                                                <p className="text-sm font-semibold">{badge.title}</p>
                                                <p className="text-xs opacity-80">{badge.description}</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </section>

                        <section className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                            <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
                                <div>
                                    <h2 className="text-lg font-semibold text-[#0f172a]">AI-powered doubt solver</h2>
                                    <p className="text-sm text-[#475569]">
                                        Ask a question and the AI mentor replies instantly with contextual examples and follow-up prompts.
                                    </p>
                                </div>
                                <Link
                                    to="/student/doubt-support"
                                    className="inline-flex items-center gap-2 rounded-full bg-[#0ea5e9] px-5 py-2 text-sm font-semibold text-white shadow transition hover:-translate-y-0.5 hover:bg-[#0284c7]"
                                >
                                    <MessageSquare className="h-4 w-4" />
                                    Open demo chat
                                </Link>
                            </div>
                        </section>
                    </div>
                </section>
            </div>
        </DashboardLayout>
    );
};

export default StudentDashboard;
