import React, { useState, useEffect, useMemo } from 'react';
import DashboardLayout from '../components/Layout/DashboardLayout';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import {
    GraduationCap,
    Users2,
    BookOpen,
    CalendarClock,
    BarChart3,
    ArrowRight,
    Gauge,
    LineChart,
    ClipboardCheck,
    UserPlus
} from 'lucide-react';
import { coursesAPI, progressAPI, gamificationAPI } from '../services/api';

const TeacherDashboard = () => {
    const { user } = useAuth();
    const [courses, setCourses] = useState([]);
    const [classNodes, setClassNodes] = useState([]);
    const [comparisonSeries, setComparisonSeries] = useState([]);
    const [conceptWeakness, setConceptWeakness] = useState([]);
    const [studentList, setStudentList] = useState([]);
    const [totalLearners, setTotalLearners] = useState(0);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchDashboard = async () => {
            try {
                // Get teacher's courses
                const coursesRes = await coursesAPI.getAll({ createdBy: 'me' });
                const myCourses = coursesRes.data?.data || [];
                setCourses(myCourses);

                if (myCourses.length > 0) {
                    const firstCourse = myCourses[0];
                    setTotalLearners(myCourses.reduce((sum, c) => sum + (c.enrolledStudents?.length ?? 0), 0));

                    // Fetch class progress for first course
                    try {
                        const classRes = await progressAPI.getClassProgress(firstCourse._id);
                        const classData = classRes.data?.data || {};

                        // Build class nodes from topic averages
                        if (classData.topicAverages) {
                            setClassNodes(
                                classData.topicAverages.map((t) => [t.title || t.topic, t.averageMastery ?? 0])
                            );
                        }

                        // Build student comparison from student summaries
                        if (classData.studentSummaries) {
                            setComparisonSeries(
                                classData.studentSummaries.slice(0, 6).map((s) => ({
                                    name: s.studentName || 'Student',
                                    mastery: Math.round((s.averageMastery ?? 0) * 100),
                                    trend: s.topicsCompleted > 0 ? `+${s.topicsCompleted}` : '0'
                                }))
                            );

                            // Student spotlight
                            setStudentList(
                                classData.studentSummaries.slice(0, 4).map((s) => {
                                    const m = s.averageMastery ?? 0;
                                    const status = m >= 0.8 ? 'Accelerating' : m >= 0.5 ? 'Consistent' : 'Needs support';
                                    const colors = {
                                        'Accelerating': 'bg-[#dcfce7] text-[#166534]',
                                        'Consistent': 'bg-[#dbeafe] text-[#1d4ed8]',
                                        'Needs support': 'bg-[#fee2e2] text-[#991b1b]'
                                    };
                                    return {
                                        name: s.studentName || 'Student',
                                        status,
                                        avatar: (s.studentName || 'S').split(' ').map(w => w[0]).join('').toUpperCase().slice(0, 2),
                                        color: colors[status]
                                    };
                                })
                            );
                        }

                        // Build concept weakness from low-mastery topics
                        if (classData.topicAverages) {
                            setConceptWeakness(
                                classData.topicAverages
                                    .filter((t) => t.averageMastery < 0.6)
                                    .sort((a, b) => a.averageMastery - b.averageMastery)
                                    .slice(0, 5)
                                    .map((t) => ({
                                        concept: t.title || t.topic,
                                        severity: t.averageMastery < 0.3 ? 'High' : t.averageMastery < 0.5 ? 'Medium' : 'Monitor',
                                        action: t.averageMastery < 0.3
                                            ? 'Schedule revision lab'
                                            : t.averageMastery < 0.5
                                                ? 'Assign practice set'
                                                : 'Review after quiz'
                                    }))
                            );
                        }
                    } catch {
                        // class progress not available yet, use fallbacks
                    }
                }
            } catch {
                // gracefully degrade
            } finally {
                setLoading(false);
            }
        };
        fetchDashboard();
    }, []);

    const getClassMastery = () => {
        if (classNodes.length === 0) return 0;
        return Math.round(classNodes.reduce((acc, [, value]) => acc + value, 0) / classNodes.length * 100);
    };

    const analyticsCards = useMemo(
        () => [
            {
                label: 'Courses managed',
                value: courses.length || 0,
                icon: BookOpen,
                accent: 'bg-[#ede9fe] text-[#4338ca]',
                detail: `${courses.filter(c => c.isPublished).length} published`
            },
            {
                label: 'Active learners',
                value: totalLearners,
                icon: Users2,
                accent: 'bg-[#cffafe] text-[#0f766e]',
                detail: `Across ${courses.length} course${courses.length !== 1 ? 's' : ''}`
            },
            {
                label: 'Average mastery',
                value: `${getClassMastery()}%`,
                icon: Gauge,
                accent: 'bg-[#fef9c3] text-[#92400e]',
                detail: `${classNodes.length} concepts tracked`
            },
            {
                label: 'Weak concepts',
                value: conceptWeakness.length,
                icon: ClipboardCheck,
                accent: 'bg-[#bfdbfe] text-[#1d4ed8]',
                detail: 'Need intervention'
            }
        ],
        [courses, totalLearners, classNodes, conceptWeakness]
    );

    if (loading) {
        return (
            <DashboardLayout>
                <div className="flex items-center justify-center py-24">
                    <div className="h-10 w-10 animate-spin rounded-full border-4 border-[#e2e8f0] border-t-[#4338ca]" />
                </div>
            </DashboardLayout>
        );
    }

    return (
        <DashboardLayout>
            <div className="space-y-6">
                <section className="rounded-[28px] bg-gradient-to-br from-[#f0f9ff] via-[#fef3c7] to-[#ede9fe] p-6 shadow-xl">
                    <div className="flex flex-col gap-6 lg:flex-row lg:items-center lg:justify-between">
                        <div>
                            <p className="text-xs font-semibold uppercase tracking-[0.32em] text-[#4338ca]">
                                Cohort intelligence hub
                            </p>
                            <h1 className="mt-3 text-3xl font-semibold text-[#0f172a]">
                                Good morning, {user?.name ?? 'Mentor'}
                            </h1>
                            <p className="mt-2 text-sm text-[#475569]">
                                Your classes updated overnight. Review mastery hotspots, compare learners and deploy interventions with one click.
                            </p>
                        </div>
                        <div className="rounded-3xl bg-white/80 p-5 shadow-lg">
                            <div className="flex items-center justify-between text-sm text-[#475569]">
                                <span>Cohorts monitored</span>
                                <strong className="text-[#4338ca]">4</strong>
                            </div>
                            <div className="mt-3 h-2 rounded-full bg-[#e2e8f0]">
                                <div className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] to-[#0ea5e9]" style={{ width: '68%' }} />
                            </div>
                            <p className="mt-2 text-xs uppercase tracking-[0.3em] text-[#94a3b8]">Daily sync complete</p>
                        </div>
                    </div>
                </section>

                <section className="grid gap-6 lg:grid-cols-4">
                    {analyticsCards.map((card) => (
                        <div
                            key={card.label}
                            className="flex h-full flex-col rounded-[26px] bg-white p-5 shadow-xl ring-1 ring-[#e2e8f0] transition hover:-translate-y-1 hover:shadow-2xl"
                        >
                            <div className={`mb-4 flex h-12 w-12 items-center justify-center rounded-2xl ${card.accent}`}>
                                <card.icon className="h-6 w-6" />
                            </div>
                            <p className="text-xs uppercase tracking-[0.28em] text-[#94a3b8]">{card.label}</p>
                            <p className="mt-2 text-3xl font-semibold text-[#111827]">{card.value}</p>
                            <p className="mt-1 text-sm text-[#475569]">{card.detail}</p>
                        </div>
                    ))}
                </section>

                <section className="grid gap-6 lg:grid-cols-[1.3fr,0.7fr]">
                    <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                        <div className="flex flex-col gap-3 lg:flex-row lg:items-center lg:justify-between">
                            <div>
                                <h2 className="text-lg font-semibold text-[#0f172a]">Class knowledge heatmap</h2>
                                <p className="text-sm text-[#475569]">
                                    Each tile shows concept mastery across the cohort. Darker shades = stronger proficiency.
                                </p>
                            </div>
                            <span className="rounded-full bg-[#cffafe] px-4 py-1 text-xs font-semibold uppercase tracking-widest text-[#0f766e]">
                                Auto refreshed
                            </span>
                        </div>
                        <div className="mt-6 grid gap-3 md:grid-cols-3">
                            {classNodes.length > 0 ? classNodes.map(([label, mastery]) => (
                                <div
                                    key={label}
                                    className="rounded-3xl bg-[#f8fafc] p-4 shadow-inner transition hover:-translate-y-1"
                                >
                                    <p className="text-sm font-semibold text-[#0f172a]">{label}</p>
                                    <div className="mt-3 h-2 rounded-full bg-[#e2e8f0]">
                                        <div
                                            className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] to-[#2563eb]"
                                            style={{ width: `${Math.round(mastery * 100)}%` }}
                                        />
                                    </div>
                                    <p className="mt-2 text-xs text-[#475569]">Mastery {Math.round(mastery * 100)}%</p>
                                </div>
                            )) : (
                                <div className="col-span-3 rounded-3xl bg-[#f1f5f9] p-6 text-sm text-[#64748b]">
                                    No class progress data available yet. Students need to start studying.
                                </div>
                            )}
                        </div>
                        <div className="mt-4 flex items-center justify-between text-xs text-[#94a3b8]">
                            <span>Needs attention</span>
                            <span>Excelling</span>
                        </div>
                    </div>

                    <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                        <div className="flex items-center justify-between">
                            <h2 className="text-lg font-semibold text-[#0f172a]">Upcoming schedule</h2>
                            <CalendarClock className="h-5 w-5 text-[#4338ca]" />
                        </div>
                        <div className="mt-5 space-y-4">
                            {[
                                { subject: 'Mechanics lab', time: '09:00', learners: 32 },
                                { subject: 'Wave optics seminar', time: '11:30', learners: 24 },
                                { subject: 'Mentor office hours', time: '14:00', learners: 12 }
                            ].map((item) => (
                                <div key={item.subject} className="rounded-2xl bg-[#f8fafc] p-4 transition hover:-translate-y-0.5">
                                    <div className="flex items-center justify-between">
                                        <p className="text-sm font-semibold text-[#111827]">{item.subject}</p>
                                        <span className="text-xs uppercase tracking-[0.28em] text-[#94a3b8]">{item.time}</span>
                                    </div>
                                    <p className="mt-2 text-xs text-[#475569]">{item.learners} learners enrolled</p>
                                </div>
                            ))}
                        </div>
                        <Link
                            to="/teacher/students"
                            className="mt-6 inline-flex items-center gap-2 text-sm font-semibold text-[#4338ca] transition hover:text-[#312e81]"
                        >
                            Manage class roster
                            <ArrowRight className="h-4 w-4" />
                        </Link>
                    </div>
                </section>

                <section className="grid gap-6 lg:grid-cols-[1.1fr,0.9fr]">
                    <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                        <div className="flex items-center justify-between">
                            <h2 className="text-lg font-semibold text-[#0f172a]">Student comparison</h2>
                            <BarChart3 className="h-5 w-5 text-[#4338ca]" />
                        </div>
                        <div className="mt-5 space-y-3">
                            {comparisonSeries.map((student) => (
                                <div key={student.name} className="rounded-2xl bg-[#f8fafc] p-4 transition hover:-translate-y-0.5">
                                    <div className="flex items-center justify-between">
                                        <div>
                                            <p className="text-sm font-semibold text-[#0f172a]">{student.name}</p>
                                            <p className="text-xs text-[#94a3b8]">Trend {student.trend}</p>
                                        </div>
                                        <span className="text-lg font-semibold text-[#4338ca]">{student.mastery}%</span>
                                    </div>
                                    <div className="mt-3 h-2 rounded-full bg-[#e2e8f0]">
                                        <div
                                            className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] to-[#0ea5e9]"
                                            style={{ width: `${student.mastery}%` }}
                                        />
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                        <div className="flex items-center justify-between">
                            <h2 className="text-lg font-semibold text-[#0f172a]">Concept weakness tracker</h2>
                            <LineChart className="h-5 w-5 text-[#0ea5e9]" />
                        </div>
                        <div className="mt-5 overflow-hidden rounded-2xl border border-[#e2e8f0]">
                            <table className="w-full text-sm text-[#334155]">
                                <thead className="bg-[#f8fafc] text-xs uppercase tracking-[0.18em] text-[#94a3b8]">
                                    <tr>
                                        <th className="px-4 py-3 text-left">Concept</th>
                                        <th className="px-4 py-3 text-left">Severity</th>
                                        <th className="px-4 py-3 text-left">Recommended action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {conceptWeakness.map((row) => (
                                        <tr key={row.concept} className="border-t border-[#e2e8f0] bg-white transition hover:bg-[#f8fafc]">
                                            <td className="px-4 py-3 font-semibold text-[#111827]">{row.concept}</td>
                                            <td className="px-4 py-3">
                                                <span
                                                    className={`rounded-full px-3 py-1 text-xs font-semibold ${row.severity === 'High'
                                                            ? 'bg-[#fee2e2] text-[#b91c1c]'
                                                            : row.severity === 'Medium'
                                                                ? 'bg-[#fef9c3] text-[#92400e]'
                                                                : 'bg-[#dcfce7] text-[#166534]'
                                                        }`}
                                                >
                                                    {row.severity}
                                                </span>
                                            </td>
                                            <td className="px-4 py-3 text-sm text-[#475569]">{row.action}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </section>

                <section className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                    <div className="flex items-center justify-between">
                        <h2 className="text-lg font-semibold text-[#0f172a]">Student spotlight</h2>
                        <GraduationCap className="h-5 w-5 text-[#4338ca]" />
                    </div>
                    <div className="mt-5 grid gap-4 md:grid-cols-2 lg:grid-cols-4">
                        {studentList.map((student) => (
                            <div key={student.name} className="rounded-3xl bg-[#f8fafc] p-4 transition hover:-translate-y-1 hover:shadow-xl">
                                <div className="flex items-center gap-3">
                                    <span className={`flex h-10 w-10 items-center justify-center rounded-full text-sm font-semibold ${student.color}`}>
                                        {student.avatar}
                                    </span>
                                    <div>
                                        <p className="text-sm font-semibold text-[#0f172a]">{student.name}</p>
                                        <p className="text-xs text-[#64748b]">{student.status}</p>
                                    </div>
                                </div>
                                <Link
                                    to="/teacher/students"
                                    className="mt-4 inline-flex items-center gap-2 text-xs font-semibold text-[#4338ca] transition hover:text-[#312e81]"
                                >
                                    Open profile
                                    <ArrowRight className="h-4 w-4" />
                                </Link>
                            </div>
                        ))}
                    </div>
                </section>

                <section className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                    <div className="flex flex-col gap-4 lg:flex-row lg:items-center lg:justify-between">
                        <div>
                            <h2 className="text-lg font-semibold text-[#0f172a]">Deploy interventions</h2>
                            <p className="text-sm text-[#475569]">
                                Create custom revision playlists or escalate support tickets. ML recommendations refresh every six hours.
                            </p>
                        </div>
                        <Link
                            to="/teacher/students"
                            className="inline-flex items-center gap-2 rounded-full bg-[#4338ca] px-5 py-2 text-sm font-semibold text-white shadow transition hover:-translate-y-0.5 hover:bg-[#312e81]"
                        >
                            <UserPlus className="h-4 w-4" />
                            Launch intervention
                        </Link>
                    </div>
                </section>
            </div>
        </DashboardLayout>
    );
};

export default TeacherDashboard;
