import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import DashboardLayout from '../components/Layout/DashboardLayout';
import { coursesAPI, progressAPI } from '../services/api';
import { ArrowLeft, BookOpen, Clock, Trophy, CheckCircle, Circle, Loader2 } from 'lucide-react';

const STATUS_STYLES = {
    'mastered': { bg: 'bg-[#dcfce7]', text: 'text-[#166534]', label: 'Mastered' },
    'completed': { bg: 'bg-[#dbeafe]', text: 'text-[#1d4ed8]', label: 'Completed' },
    'in-progress': { bg: 'bg-[#fef9c3]', text: 'text-[#92400e]', label: 'In Progress' },
    'not-started': { bg: 'bg-[#f1f5f9]', text: 'text-[#64748b]', label: 'Not Started' }
};

const StudentCourseDetail = () => {
    const { courseId } = useParams();
    const [course, setCourse] = useState(null);
    const [topics, setTopics] = useState([]);
    const [progressMap, setProgressMap] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [updating, setUpdating] = useState(null); // topicId being updated

    const fetchData = useCallback(async () => {
        try {
            const [courseRes, progressRes] = await Promise.all([
                coursesAPI.getById(courseId),
                progressAPI.getCourseProgress(courseId).catch(() => ({ data: { topics: [] } }))
            ]);
            setCourse(courseRes.data?.course || null);
            setTopics(courseRes.data?.topics || []);

            // Build progress lookup by topic ID
            const pMap = {};
            const progressArr = progressRes.data?.progress || [];
            if (Array.isArray(progressArr)) {
                progressArr.forEach(p => {
                    const topicId = p.topic?._id || p.topic;
                    if (topicId) pMap[topicId] = p;
                });
            }
            setProgressMap(pMap);
        } catch {
            setError('Failed to load course details.');
        } finally {
            setLoading(false);
        }
    }, [courseId]);

    useEffect(() => { fetchData(); }, [fetchData]);

    const handleUpdateProgress = async (topicId, status, score = null) => {
        setUpdating(topicId);
        try {
            const data = { status };
            if (score !== null) data.lastScore = score;
            data.timeSpentMinutes = 15;
            await progressAPI.updateProgress(topicId, data);
            await fetchData();
        } catch {
            setError('Failed to update progress.');
        } finally {
            setUpdating(null);
        }
    };

    const getProgress = (topicId) => progressMap[topicId] || null;

    if (loading) {
        return (
            <DashboardLayout>
                <div className="flex items-center justify-center py-24">
                    <div className="h-10 w-10 animate-spin rounded-full border-4 border-[#e2e8f0] border-t-[#4338ca]" />
                </div>
            </DashboardLayout>
        );
    }

    const completedCount = topics.filter(t => {
        const p = getProgress(t._id);
        return p?.status === 'completed' || p?.status === 'mastered';
    }).length;

    return (
        <DashboardLayout>
            <div className="space-y-6">
                {/* Back link + Course header */}
                <section className="rounded-[28px] bg-gradient-to-br from-[#ede9fe] via-[#f0f9ff] to-[#fef9c3] p-6 shadow-xl">
                    <Link to="/student" className="inline-flex items-center gap-1 text-sm font-medium text-[#4338ca] hover:text-[#312e81] mb-4">
                        <ArrowLeft className="h-4 w-4" /> Back to Dashboard
                    </Link>
                    <h1 className="text-2xl font-semibold text-[#0f172a]">{course?.title || 'Course'}</h1>
                    <p className="mt-1 text-sm text-[#475569]">{course?.description}</p>
                    <div className="mt-4 flex flex-wrap gap-4 text-sm text-[#475569]">
                        <span className="flex items-center gap-1"><BookOpen className="h-4 w-4" /> {course?.subject}</span>
                        <span className="flex items-center gap-1"><Clock className="h-4 w-4" /> {course?.estimatedHours}h estimated</span>
                        <span className="flex items-center gap-1"><Trophy className="h-4 w-4" /> {completedCount}/{topics.length} completed</span>
                    </div>
                    {/* Progress bar */}
                    <div className="mt-4 h-2 rounded-full bg-white/60">
                        <div className="h-2 rounded-full bg-gradient-to-r from-[#4338ca] to-[#0ea5e9] transition-all"
                            style={{ width: `${topics.length > 0 ? Math.round((completedCount / topics.length) * 100) : 0}%` }} />
                    </div>
                </section>

                {error && <div className="rounded-2xl bg-red-50 p-4 text-sm text-red-700">{error}</div>}

                {/* Topics list */}
                <section className="rounded-[28px] bg-white p-6 shadow-xl ring-1 ring-[#e2e8f0]">
                    <h2 className="text-lg font-semibold text-[#0f172a] mb-4">Course Topics</h2>
                    {topics.length === 0 ? (
                        <p className="py-8 text-center text-sm text-[#94a3b8]">No topics in this course yet.</p>
                    ) : (
                        <div className="space-y-3">
                            {topics.sort((a, b) => a.order - b.order).map((topic) => {
                                const prog = getProgress(topic._id);
                                const status = prog?.status || 'not-started';
                                const style = STATUS_STYLES[status] || STATUS_STYLES['not-started'];
                                const mastery = prog?.masteryLevel ?? 0;
                                const isUpdating = updating === topic._id;

                                return (
                                    <div key={topic._id} className="rounded-2xl border border-[#e2e8f0] bg-[#f8fafc] p-5 transition hover:shadow-md">
                                        <div className="flex flex-col gap-3 lg:flex-row lg:items-center lg:justify-between">
                                            <div className="flex items-start gap-3">
                                                <div className="mt-1">
                                                    {status === 'completed' || status === 'mastered' ? (
                                                        <CheckCircle className="h-5 w-5 text-[#16a34a]" />
                                                    ) : (
                                                        <Circle className="h-5 w-5 text-[#cbd5e1]" />
                                                    )}
                                                </div>
                                                <div>
                                                    <div className="flex items-center gap-2">
                                                        <span className="text-xs font-bold text-[#4338ca]">#{topic.order}</span>
                                                        <h3 className="text-sm font-semibold text-[#0f172a]">{topic.title}</h3>
                                                        <span className={`rounded-full px-2 py-0.5 text-xs font-medium ${style.bg} ${style.text}`}>{style.label}</span>
                                                    </div>
                                                    {topic.description && <p className="mt-1 text-xs text-[#64748b]">{topic.description}</p>}
                                                    <div className="mt-1 flex gap-3 text-xs text-[#94a3b8]">
                                                        <span>{topic.contentType}</span>
                                                        <span>{topic.estimatedMinutes} min</span>
                                                        <span>{topic.pointsReward} XP</span>
                                                        {mastery > 0 && <span>Mastery: {Math.round(mastery * 100)}%</span>}
                                                    </div>
                                                </div>
                                            </div>

                                            {/* Action buttons */}
                                            <div className="flex gap-2">
                                                {isUpdating ? (
                                                    <Loader2 className="h-5 w-5 animate-spin text-[#4338ca]" />
                                                ) : status === 'not-started' ? (
                                                    <button onClick={() => handleUpdateProgress(topic._id, 'in-progress')}
                                                        className="rounded-full bg-[#4338ca] px-4 py-1.5 text-xs font-semibold text-white hover:bg-[#312e81]">
                                                        Start Learning
                                                    </button>
                                                ) : status === 'in-progress' ? (
                                                    <button onClick={() => handleUpdateProgress(topic._id, 'completed', 85)}
                                                        className="rounded-full bg-[#16a34a] px-4 py-1.5 text-xs font-semibold text-white hover:bg-[#15803d]">
                                                        Mark Complete
                                                    </button>
                                                ) : status === 'completed' ? (
                                                    <button onClick={() => handleUpdateProgress(topic._id, 'mastered', 95)}
                                                        className="rounded-full bg-[#f59e0b] px-4 py-1.5 text-xs font-semibold text-white hover:bg-[#d97706]">
                                                        Master It
                                                    </button>
                                                ) : (
                                                    <span className="rounded-full bg-[#dcfce7] px-4 py-1.5 text-xs font-semibold text-[#166534]">âœ“ Mastered</span>
                                                )}
                                            </div>
                                        </div>

                                        {/* Mastery progress bar */}
                                        {mastery > 0 && (
                                            <div className="mt-3 h-1.5 rounded-full bg-[#e2e8f0]">
                                                <div className="h-1.5 rounded-full bg-gradient-to-r from-[#4338ca] to-[#0ea5e9] transition-all"
                                                    style={{ width: `${Math.round(mastery * 100)}%` }} />
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                        </div>
                    )}
                </section>
            </div>
        </DashboardLayout>
    );
};

export default StudentCourseDetail;
