import React, { useState, useEffect, useRef } from 'react';
import DashboardLayout from '../components/Layout/DashboardLayout';
import { doubtsAPI, materialsAPI } from '../services/api';
import {
    MessageCircle,
    Send,
    Clock,
    BookOpen,
    Sparkles,
    ChevronDown,
    X,
    AlertCircle,
    User,
    Bot,
    Loader,
    History,
    FileText,
    CheckCircle,
    HelpCircle
} from 'lucide-react';

const AIDoubtSupport = () => {
    const [question, setQuestion] = useState('');
    const [loading, setLoading] = useState(false);
    const [currentAnswer, setCurrentAnswer] = useState(null);
    const [history, setHistory] = useState([]);
    const [showHistory, setShowHistory] = useState(false);
    const [historyLoading, setHistoryLoading] = useState(true);
    const [error, setError] = useState('');
    const [stats, setStats] = useState(null);

    // Filters
    const [selectedSubject, setSelectedSubject] = useState('');
    const [selectedTopic, setSelectedTopic] = useState('');
    const [subjects, setSubjects] = useState([]);
    const [topics, setTopics] = useState([]);

    const answerRef = useRef(null);

    useEffect(() => {
        fetchHistory();
        fetchStats();
        fetchSubjects();
    }, []);

    useEffect(() => {
        if (selectedSubject) {
            fetchTopics(selectedSubject);
        } else {
            setTopics([]);
            setSelectedTopic('');
        }
    }, [selectedSubject]);

    useEffect(() => {
        if (currentAnswer && answerRef.current) {
            answerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
    }, [currentAnswer]);

    const fetchHistory = async () => {
        try {
            const response = await doubtsAPI.getMyDoubts();
            setHistory(response.data.doubts || []);
        } catch (error) {
            console.error('Error fetching history:', error);
        } finally {
            setHistoryLoading(false);
        }
    };

    const fetchStats = async () => {
        try {
            const response = await doubtsAPI.getStats();
            setStats(response.data);
        } catch (error) {
            console.error('Error fetching stats:', error);
        }
    };

    const fetchSubjects = async () => {
        try {
            const response = await materialsAPI.getSubjects();
            setSubjects(response.data.subjects || []);
        } catch (error) {
            console.error('Error fetching subjects:', error);
        }
    };

    const fetchTopics = async (subject) => {
        try {
            const response = await materialsAPI.getTopics(subject);
            setTopics(response.data.topics || []);
        } catch (error) {
            console.error('Error fetching topics:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!question.trim() || loading) return;

        setLoading(true);
        setError('');
        setCurrentAnswer(null);

        try {
            const payload = { question: question.trim() };
            if (selectedSubject) payload.subject = selectedSubject;
            if (selectedTopic) payload.topic = selectedTopic;

            const response = await doubtsAPI.submit(payload);
            // Normalize to match the shape used by handleHistoryClick
            const d = response.data.doubt;
            setCurrentAnswer({
                doubt: d,
                answer: d.answer,
                relevantMaterials: d.relevantMaterials
            });
            setQuestion('');
            fetchHistory();
            fetchStats();
        } catch (error) {
            console.error('Error submitting doubt:', error);
            setError(error.response?.data?.message || 'Failed to get answer. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleHistoryClick = (doubt) => {
        setCurrentAnswer({
            doubt: doubt,
            answer: doubt.answer,
            relevantMaterials: doubt.relevantMaterials
        });
        setShowHistory(false);
    };

    const formatAnswer = (text) => {
        if (!text) return null;

        // Split by newlines and format
        return text.split('\n').map((line, index) => {
            // Handle bullet points
            if (line.trim().startsWith('•') || line.trim().startsWith('-')) {
                return (
                    <li key={index} className="ml-4 mb-1">
                        {line.replace(/^[•-]\s*/, '')}
                    </li>
                );
            }
            // Handle numbered lists
            if (/^\d+[.)]\s/.test(line.trim())) {
                return (
                    <li key={index} className="ml-4 mb-1">
                        {line.replace(/^\d+[.)]\s*/, '')}
                    </li>
                );
            }
            // Handle headers (lines ending with :)
            if (line.trim().endsWith(':') && line.trim().length < 100) {
                return (
                    <h4 key={index} className="font-semibold text-gray-900 mt-4 mb-2">
                        {line}
                    </h4>
                );
            }
            // Regular paragraph
            if (line.trim()) {
                return (
                    <p key={index} className="mb-2">
                        {line}
                    </p>
                );
            }
            return null;
        });
    };

    return (
        <DashboardLayout>
            <div className="space-y-6">
                {/* Header */}
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                    <div>
                        <h1 className="text-2xl font-bold text-gray-900">AI Doubt Support</h1>
                        <p className="text-gray-600 mt-1">Ask questions and get AI-powered answers from course materials</p>
                    </div>
                    <button
                        onClick={() => setShowHistory(!showHistory)}
                        className={`inline-flex items-center gap-2 px-4 py-2.5 rounded-xl transition-all ${showHistory
                            ? 'bg-gradient-to-r from-gray-800 to-gray-900 text-white shadow-md'
                            : 'bg-white text-gray-700 border border-gray-200 hover:bg-gray-50'
                            }`}
                    >
                        <History className="w-5 h-5" />
                        History
                        {history.length > 0 && (
                            <span className={`px-2 py-0.5 text-xs rounded-full ${showHistory ? 'bg-white text-black' : 'bg-gray-100 text-gray-600'
                                }`}>
                                {history.length}
                            </span>
                        )}
                    </button>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                    <div className="bg-gradient-to-br from-gray-800 via-gray-700 to-gray-900 rounded-2xl p-5 text-white">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-gray-300 text-sm">Questions Asked</p>
                                <p className="text-3xl font-bold mt-1">{stats?.total || 0}</p>
                            </div>
                            <div className="p-3 bg-white/90 backdrop-blur rounded-xl shadow-md">
                                <HelpCircle className="w-6 h-6 text-gray-800" />
                            </div>
                        </div>
                    </div>
                    <div className="bg-white rounded-2xl p-5 border border-gray-200">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-gray-500 text-sm">Resolved</p>
                                <p className="text-3xl font-bold mt-1 text-gray-900">{stats?.answered || 0}</p>
                            </div>
                            <div className="p-3 bg-gray-100 rounded-xl">
                                <CheckCircle className="w-6 h-6 text-gray-700" />
                            </div>
                        </div>
                    </div>
                    <div className="bg-white rounded-2xl p-5 border border-gray-200">
                        <div className="flex items-center justify-between">
                            <div>
                                <p className="text-gray-500 text-sm">Success Rate</p>
                                <p className="text-3xl font-bold mt-1 text-gray-900">{stats?.successRate ? `${Math.round(stats.successRate)}%` : '—'}</p>
                            </div>
                            <div className="p-3 bg-gray-100 rounded-xl">
                                <BookOpen className="w-6 h-6 text-gray-700" />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Error */}
                {error && (
                    <div className="p-4 bg-gray-100 border border-gray-300 rounded-xl flex items-center gap-3 text-gray-700">
                        <AlertCircle className="w-5 h-5 flex-shrink-0" />
                        <span>{error}</span>
                        <button onClick={() => setError('')} className="ml-auto p-1 hover:bg-gray-200 rounded-lg">
                            <X className="w-4 h-4" />
                        </button>
                    </div>
                )}

                {/* Question Form */}
                <div className="bg-white rounded-2xl shadow-sm border border-gray-200 p-6">
                    <div className="flex items-center gap-3 mb-4">
                        <div className="p-2 bg-black rounded-xl">
                            <Sparkles className="w-5 h-5 text-white" />
                        </div>
                        <h2 className="text-lg font-semibold text-gray-900">Ask a Question</h2>
                    </div>

                    {/* Optional Filters */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-600 mb-1">
                                Subject (Optional)
                            </label>
                            <div className="relative">
                                <select
                                    value={selectedSubject}
                                    onChange={(e) => setSelectedSubject(e.target.value)}
                                    className="w-full px-4 py-2.5 bg-gray-50 border border-gray-200 rounded-xl appearance-none focus:bg-white focus:ring-2 focus:ring-black focus:border-transparent outline-none transition-all text-sm"
                                >
                                    <option value="">All Subjects</option>
                                    {subjects.map((subject) => (
                                        <option key={subject} value={subject}>{subject}</option>
                                    ))}
                                </select>
                                <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
                            </div>
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-600 mb-1">
                                Topic (Optional)
                            </label>
                            <div className="relative">
                                <select
                                    value={selectedTopic}
                                    onChange={(e) => setSelectedTopic(e.target.value)}
                                    disabled={!selectedSubject}
                                    className="w-full px-4 py-2.5 bg-gray-50 border border-gray-200 rounded-xl appearance-none focus:bg-white focus:ring-2 focus:ring-black focus:border-transparent outline-none transition-all text-sm disabled:opacity-50"
                                >
                                    <option value="">All Topics</option>
                                    {topics.map((topic) => (
                                        <option key={topic} value={topic}>{topic}</option>
                                    ))}
                                </select>
                                <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
                            </div>
                        </div>
                    </div>

                    {/* Question Input */}
                    <form onSubmit={handleSubmit}>
                        <div className="relative">
                            <textarea
                                value={question}
                                onChange={(e) => setQuestion(e.target.value)}
                                placeholder="Type your question here... The AI will search through course materials to provide a relevant answer."
                                rows={4}
                                className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:ring-2 focus:ring-black focus:border-transparent outline-none transition-all resize-none"
                            />
                        </div>
                        <div className="flex items-center justify-between mt-4">
                            <p className="text-sm text-gray-500">
                                {selectedSubject || selectedTopic ? (
                                    <>Searching in: {selectedSubject || 'All'} {selectedTopic && `> ${selectedTopic}`}</>
                                ) : (
                                    'Searching across all materials'
                                )}
                            </p>
                            <button
                                type="submit"
                                disabled={!question.trim() || loading}
                                className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-gray-800 to-gray-900 text-white rounded-xl hover:from-gray-700 hover:to-gray-800 transition-all disabled:opacity-70 disabled:cursor-not-allowed font-medium shadow-md"
                            >
                                {loading ? (
                                    <>
                                        <Loader className="w-5 h-5 animate-spin" />
                                        Processing...
                                    </>
                                ) : (
                                    <>
                                        <Send className="w-5 h-5" />
                                        Ask Question
                                    </>
                                )}
                            </button>
                        </div>
                    </form>
                </div>

                {/* Answer Display */}
                {currentAnswer && (
                    <div ref={answerRef} className="bg-white rounded-2xl shadow-sm border border-gray-200 overflow-hidden">
                        {/* Question */}
                        <div className="p-6 bg-gray-50 border-b border-gray-200">
                            <div className="flex items-start gap-3">
                                <div className="p-2 bg-white rounded-xl shadow-sm">
                                    <User className="w-5 h-5 text-gray-600" />
                                </div>
                                <div>
                                    <p className="text-sm font-medium text-gray-500">Your Question</p>
                                    <p className="text-gray-900 mt-1">{currentAnswer.doubt?.question}</p>
                                </div>
                            </div>
                        </div>

                        {/* Answer */}
                        <div className="p-6">
                            <div className="flex items-start gap-3">
                                <div className="p-2 bg-gradient-to-br from-gray-700 to-gray-900 rounded-xl shadow-md">
                                    <Bot className="w-5 h-5 text-white" />
                                </div>
                                <div className="flex-1 min-w-0">
                                    <p className="text-sm font-medium text-gray-500 mb-3">AI Answer</p>
                                    <div className="prose prose-sm max-w-none text-gray-700">
                                        {formatAnswer(currentAnswer.answer)}
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Relevant Materials */}
                        {currentAnswer.relevantMaterials && currentAnswer.relevantMaterials.length > 0 && (
                            <div className="px-6 pb-6">
                                <div className="bg-gray-50 rounded-xl p-4">
                                    <div className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-3">
                                        <FileText className="w-4 h-4" />
                                        Sources Referenced
                                    </div>
                                    <div className="space-y-2">
                                        {currentAnswer.relevantMaterials.map((material, index) => (
                                            <div
                                                key={index}
                                                className="flex items-center gap-3 p-3 bg-white rounded-lg border border-gray-200"
                                            >
                                                <div className="p-1.5 bg-gray-100 rounded-lg">
                                                    <BookOpen className="w-4 h-4 text-gray-600" />
                                                </div>
                                                <div className="flex-1 min-w-0">
                                                    <p className="text-sm font-medium text-gray-900 truncate">
                                                        {material.title}
                                                    </p>
                                                    <p className="text-xs text-gray-500">
                                                        {material.subject} • {material.topic}
                                                    </p>
                                                </div>
                                                <span className="text-xs text-gray-400 bg-gray-100 px-2 py-1 rounded">
                                                    {Math.round((material.similarity || 0) * 100)}% match
                                                </span>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                )}

                {/* Loading State */}
                {loading && !currentAnswer && (
                    <div className="bg-white rounded-2xl shadow-sm border border-gray-200 p-8 text-center">
                        <div className="w-16 h-16 bg-gradient-to-br from-gray-700 to-gray-900 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg">
                            <Sparkles className="w-8 h-8 text-white animate-pulse" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900">Searching Course Materials</h3>
                        <p className="text-gray-500 mt-1">Finding the most relevant content for your question...</p>
                        <div className="mt-4 flex justify-center gap-1">
                            <div className="w-2 h-2 bg-gradient-to-r from-gray-600 to-gray-800 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                            <div className="w-2 h-2 bg-gradient-to-r from-gray-600 to-gray-800 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                            <div className="w-2 h-2 bg-gradient-to-r from-gray-600 to-gray-800 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                        </div>
                    </div>
                )}

                {/* History Sidebar */}
                {showHistory && (
                    <div className="fixed inset-0 bg-black/60 z-50 flex justify-end" onClick={() => setShowHistory(false)}>
                        <div
                            className="bg-white w-full max-w-md h-full overflow-hidden flex flex-col"
                            onClick={(e) => e.stopPropagation()}
                        >
                            <div className="p-6 border-b border-gray-200 flex items-center justify-between">
                                <div className="flex items-center gap-3">
                                    <div className="p-2 bg-gray-100 rounded-xl">
                                        <History className="w-5 h-5 text-gray-600" />
                                    </div>
                                    <h2 className="text-lg font-semibold text-gray-900">Question History</h2>
                                </div>
                                <button
                                    onClick={() => setShowHistory(false)}
                                    className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
                                >
                                    <X className="w-5 h-5" />
                                </button>
                            </div>

                            <div className="flex-1 overflow-y-auto p-4">
                                {historyLoading ? (
                                    <div className="flex items-center justify-center h-32">
                                        <Loader className="w-6 h-6 text-gray-400 animate-spin" />
                                    </div>
                                ) : history.length === 0 ? (
                                    <div className="text-center py-12">
                                        <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                                            <MessageCircle className="w-8 h-8 text-gray-400" />
                                        </div>
                                        <h3 className="text-gray-900 font-medium">No questions yet</h3>
                                        <p className="text-gray-500 text-sm mt-1">
                                            Your question history will appear here
                                        </p>
                                    </div>
                                ) : (
                                    <div className="space-y-3">
                                        {history.map((doubt) => (
                                            <button
                                                key={doubt._id}
                                                onClick={() => handleHistoryClick(doubt)}
                                                className="w-full text-left p-4 bg-gray-50 hover:bg-gray-100 rounded-xl transition-colors group"
                                            >
                                                <p className="text-sm font-medium text-gray-900 line-clamp-2 group-hover:text-black">
                                                    {doubt.question}
                                                </p>
                                                <div className="flex items-center gap-2 mt-2 text-xs text-gray-500">
                                                    {doubt.subject && (
                                                        <>
                                                            <span className="px-2 py-0.5 bg-gray-200 rounded">
                                                                {doubt.subject}
                                                            </span>
                                                        </>
                                                    )}
                                                    <span className="flex items-center gap-1">
                                                        <Clock className="w-3 h-3" />
                                                        {new Date(doubt.createdAt).toLocaleDateString()}
                                                    </span>
                                                </div>
                                            </button>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </DashboardLayout>
    );
};

export default AIDoubtSupport;
